mapper.update:
update t_copyright set

    state = #{copyright.state}

where copyright_id = #{copyright.copyrightId}

mapper.select:
FROM t_copyright t LEFT JOIN t_members m ON (t.copyright_id=m.task_id) LEFT JOIN t_items i ON (i.items_id=t.item_id) where 1=1
AND copyright_id = #{copyrightId}
GROUP BY copyright_id
